mapper.select:
SELECT * FROM fx_channels
WHERE owner_id = (
    SELECT user_id
    FROM fx_users
    WHERE discord_id = #{discordUserId}
)

mapper.update:
UPDATE fx_channels
SET display_name = #{channelDisplayName}
WHERE discord_channel_id = #{discordChannelId}
    AND owner_id = (
        SELECT user_id
        FROM fx_users
        WHERE discord_id = #{discordUserId}
    )

mapper.delete:
DELETE FROM fx_channels
WHERE discord_channel_id = #{discordChannelId}
    AND owner_id = (
        SELECT user_id
        FROM fx_users
        WHERE discord_id = #{discordUserId}
    )
