mapper.select:
SELECT * FROM fx_user_channels
WHERE user_id = (
    SELECT user_id
    FROM fx_users
    WHERE discord_id = #{discordId}
)

mapper.insert:
INSERT INTO fx_user_channels (user_id, display_name)
SELECT user_id, #{channelDisplayName}
FROM fx_users
WHERE discord_id = #{discordId}
    AND user_id NOT IN(
        SELECT user_id FROM fx_user_channels
    )

mapper.update:
UPDATE fx_user_channels
SET display_name = #{channelDisplayName}
WHERE user_id = (
    SELECT user_id
    FROM fx_users
    WHERE discord_id = #{discordId}
)
